import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { Slider } from "@/components/ui/slider";
import { useClipboard } from "@/hooks/use-clipboard";
import { useToast } from "@/hooks/use-toast";
import { generatePassword, type PasswordOptions } from "@/lib/crypto-utils";
import { Copy, RefreshCw, Shield } from "lucide-react";

export default function PasswordGenerator() {
  const [password, setPassword] = useState("");
  const [options, setOptions] = useState<PasswordOptions>({
    length: 12,
    includeUppercase: true,
    includeLowercase: true,
    includeNumbers: true,
    includeSymbols: false,
  });

  const { copyToClipboard } = useClipboard();
  const { toast } = useToast();

  const handleGenerate = () => {
    const hasAtLeastOneOption = 
      options.includeUppercase || 
      options.includeLowercase || 
      options.includeNumbers || 
      options.includeSymbols;

    if (!hasAtLeastOneOption) {
      toast({
        title: "Error",
        description: "Please select at least one character type",
        variant: "destructive",
      });
      return;
    }

    const newPassword = generatePassword(options);
    setPassword(newPassword);
  };

  const updateOption = (key: keyof PasswordOptions, value: boolean | number) => {
    setOptions(prev => ({ ...prev, [key]: value }));
  };

  const getStrengthColor = () => {
    let score = 0;
    if (options.includeUppercase) score++;
    if (options.includeLowercase) score++;
    if (options.includeNumbers) score++;
    if (options.includeSymbols) score++;

    if (options.length >= 12) score++;
    if (options.length >= 16) score++;

    if (score <= 2) return "text-red-500";
    if (score <= 4) return "text-yellow-500";
    return "text-green-500";
  };

  const getStrengthText = () => {
    let score = 0;
    if (options.includeUppercase) score++;
    if (options.includeLowercase) score++;
    if (options.includeNumbers) score++;
    if (options.includeSymbols) score++;

    if (options.length >= 12) score++;
    if (options.length >= 16) score++;

    if (score <= 2) return "Weak";
    if (score <= 4) return "Medium";
    return "Strong";
  };

  return (
    <div className="space-y-6">
      {/* Password Display */}
      {password && (
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold">Generated Password</h3>
              <div className="flex gap-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => copyToClipboard(password, "Password copied to clipboard")}
                  data-testid="button-copy-password"
                >
                  <Copy className="w-4 h-4" />
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={handleGenerate}
                  data-testid="button-regenerate"
                >
                  <RefreshCw className="w-4 h-4" />
                </Button>
              </div>
            </div>
            <div className="bg-muted p-4 rounded-md">
              <span className="text-lg font-mono break-all" data-testid="text-password">
                {password}
              </span>
            </div>
            <div className="mt-2 flex items-center justify-between text-sm">
              <span className="text-muted-foreground">{password.length} characters</span>
              <span className={`font-medium ${getStrengthColor()}`}>
                Strength: {getStrengthText()}
              </span>
            </div>
          </CardContent>
        </Card>
      )}

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Password Options */}
        <Card>
          <CardHeader>
            <CardTitle>Password Options</CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            {/* Length Slider */}
            <div className="space-y-3">
              <div className="flex justify-between">
                <label className="text-sm font-medium">Length</label>
                <span className="text-sm text-muted-foreground">{options.length} characters</span>
              </div>
              <Slider
                value={[options.length]}
                onValueChange={([value]) => updateOption('length', value)}
                min={4}
                max={50}
                step={1}
                className="w-full"
                data-testid="slider-length"
              />
              <div className="flex justify-between text-xs text-muted-foreground">
                <span>4</span>
                <span>50</span>
              </div>
            </div>

            {/* Character Options */}
            <div className="space-y-4">
              <h4 className="text-sm font-medium">Include Characters</h4>

              <div className="space-y-3">
                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="uppercase"
                    checked={options.includeUppercase}
                    onCheckedChange={(checked) => updateOption('includeUppercase', !!checked)}
                    data-testid="checkbox-uppercase"
                  />
                  <label htmlFor="uppercase" className="text-sm">
                    Uppercase Letters (A-Z)
                  </label>
                </div>

                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="lowercase"
                    checked={options.includeLowercase}
                    onCheckedChange={(checked) => updateOption('includeLowercase', !!checked)}
                    data-testid="checkbox-lowercase"
                  />
                  <label htmlFor="lowercase" className="text-sm">
                    Lowercase Letters (a-z)
                  </label>
                </div>

                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="numbers"
                    checked={options.includeNumbers}
                    onCheckedChange={(checked) => updateOption('includeNumbers', !!checked)}
                    data-testid="checkbox-numbers"
                  />
                  <label htmlFor="numbers" className="text-sm">
                    Numbers (0-9)
                  </label>
                </div>

                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="symbols"
                    checked={options.includeSymbols}
                    onCheckedChange={(checked) => updateOption('includeSymbols', !!checked)}
                    data-testid="checkbox-symbols"
                  />
                  <label htmlFor="symbols" className="text-sm">
                    Symbols (!@#$%^&*)
                  </label>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Security Tips */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Shield className="w-5 h-5" />
              <span>Security Tips</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-3 text-sm">
              <div className="flex items-start space-x-2">
                <div className="w-2 h-2 bg-primary rounded-full mt-1.5"></div>
                <span>Use at least 12 characters for better security</span>
              </div>
              <div className="flex items-start space-x-2">
                <div className="w-2 h-2 bg-primary rounded-full mt-1.5"></div>
                <span>Include a mix of character types</span>
              </div>
              <div className="flex items-start space-x-2">
                <div className="w-2 h-2 bg-primary rounded-full mt-1.5"></div>
                <span>Use unique passwords for each account</span>
              </div>
              <div className="flex items-start space-x-2">
                <div className="w-2 h-2 bg-primary rounded-full mt-1.5"></div>
                <span>Consider using a password manager</span>
              </div>
              <div className="flex items-start space-x-2">
                <div className="w-2 h-2 bg-primary rounded-full mt-1.5"></div>
                <span>Enable two-factor authentication when possible</span>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Generate Button */}
      <Card>
        <CardContent className="p-6">
          <Button 
            onClick={handleGenerate} 
            size="lg"
            className="w-full"
            data-testid="button-generate"
          >
            <Shield className="w-5 h-5 mr-2" />
            Generate Secure Password
          </Button>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/qr-code.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Label } from "@/components/ui/label";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { useClipboard } from "@/hooks/use-clipboard";
import { useToast } from "@/hooks/use-toast";
import { QrCode, Download, Copy, Settings, Palette, Info } from "lucide-react";

type QRType = "text" | "url" | "email" | "phone";

interface QRSettings {
  size: number;
  errorCorrectionLevel: 'L' | 'M' | 'Q' | 'H';
  margin: number;
  foregroundColor: string;
  backgroundColor: string;
}

interface QRFormData {
  type: QRType;
  content: string;
  settings: QRSettings;
}

export default function QRCodeGenerator() {
  const [formData, setFormData] = useState<QRFormData>({
    type: "text",
    content: "",
    settings: {
      size: 300,
      errorCorrectionLevel: 'M',
      margin: 4,
      foregroundColor: '#000000',
      backgroundColor: '#FFFFFF'
    }
  });
  const [qrCodeUrl, setQrCodeUrl] = useState("");
  const { copyToClipboard } = useClipboard();
  const { toast } = useToast();

  const validateInput = (): boolean => {
    if (!formData.content.trim()) {
      toast({
        title: "Error",
        description: "Please enter content to generate QR code",
        variant: "destructive",
      });
      return false;
    }

    switch (formData.type) {
      case "url":
        try {
          new URL(formData.content);
        } catch {
          toast({
            title: "Error",
            description: "Invalid URL format",
            variant: "destructive",
          });
          return false;
        }
        break;
      case "email":
        if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.content)) {
          toast({
            title: "Error",
            description: "Invalid email format",
            variant: "destructive",
          });
          return false;
        }
        break;
      case "phone":
        if (!/^[\d\s\-\+\(\)]+$/.test(formData.content)) {
          toast({
            title: "Error",
            description: "Invalid phone format",
            variant: "destructive",
          });
          return false;
        }
        break;
    }

    return true;
  };

  const formatContent = (): string => {
    switch (formData.type) {
      case "email":
        return `mailto:${formData.content}`;
      case "phone":
        return `tel:${formData.content}`;
      default:
        return formData.content;
    }
  };

  const generateQRCode = async () => {
    if (!validateInput()) return;

    const content = formatContent();
    const { settings } = formData;

    // Using QR Server API with enhanced customization
    const params = new URLSearchParams({
      data: content,
      size: `${settings.size}x${settings.size}`,
      ecc: settings.errorCorrectionLevel,
      margin: settings.margin.toString(),
      color: settings.foregroundColor.replace('#', ''),
      bgcolor: settings.backgroundColor.replace('#', ''),
      qzone: '0',  // Quiet zone
      format: 'png'
    });

    const qrApiUrl = `https://api.qrserver.com/v1/create-qr-code/?${params.toString()}`;
    setQrCodeUrl(qrApiUrl);
  };

  const downloadQRCode = async () => {
    if (!qrCodeUrl) return;

    try {
      const response = await fetch(qrCodeUrl);
      const blob = await response.blob();
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `qr-code-${formData.settings.size}x${formData.settings.size}-${Date.now()}.png`;
      link.click();
      URL.revokeObjectURL(url);

      toast({
        title: "Success",
        description: "QR code downloaded successfully",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to download QR code",
        variant: "destructive",
      });
    }
  };

  const resetToDefaults = () => {
    setFormData(prev => ({
      ...prev,
      settings: {
        size: 300,
        errorCorrectionLevel: 'M',
        margin: 4,
        foregroundColor: '#000000',
        backgroundColor: '#FFFFFF'
      }
    }));
  };

  const getErrorCorrectionDescription = (level: string) => {
    switch (level) {
      case 'L': return 'Low (~7%)';
      case 'M': return 'Medium (~15%)';
      case 'Q': return 'Quartile (~25%)';
      case 'H': return 'High (~30%)';
      default: return '';
    }
  };

  const getPlaceholder = (): string => {
    switch (formData.type) {
      case "url":
        return "https://example.com";
      case "email":
        return "user@example.com";
      case "phone":
        return "+1 (555) 123-4567";
      default:
        return "Enter your text here...";
    }
  };

  return (
    <div className="space-y-6">
      <Tabs defaultValue="content" className="w-full">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="content" className="flex items-center gap-2">
            <QrCode className="w-4 h-4" />
            Content
          </TabsTrigger>
          <TabsTrigger value="customize" className="flex items-center gap-2">
            <Settings className="w-4 h-4" />
            Customize
          </TabsTrigger>
          <TabsTrigger value="preview" className="flex items-center gap-2">
            <Palette className="w-4 h-4" />
            Preview
          </TabsTrigger>
        </TabsList>

        {/* Content Tab */}
        <TabsContent value="content" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>QR Code Content</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="content-type">Content Type</Label>
                <Select 
                  value={formData.type} 
                  onValueChange={(value: QRType) => setFormData(prev => ({ ...prev, type: value }))}
                >
                  <SelectTrigger data-testid="select-type">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="text">Plain Text</SelectItem>
                    <SelectItem value="url">URL</SelectItem>
                    <SelectItem value="email">Email</SelectItem>
                    <SelectItem value="phone">Phone</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label htmlFor="content">Content</Label>
                {formData.type === "text" ? (
                  <Textarea
                    id="content"
                    placeholder={getPlaceholder()}
                    value={formData.content}
                    onChange={(e) => setFormData(prev => ({ ...prev, content: e.target.value }))}
                    className="min-h-32"
                    data-testid="textarea-content"
                  />
                ) : (
                  <Input
                    id="content"
                    placeholder={getPlaceholder()}
                    value={formData.content}
                    onChange={(e) => setFormData(prev => ({ ...prev, content: e.target.value }))}
                    data-testid="input-content"
                  />
                )}
                <p className="text-xs text-muted-foreground">
                  {formData.content.length} characters
                </p>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Customize Tab */}
        <TabsContent value="customize" className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Size and Quality Settings */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Settings className="w-4 h-4" />
                  Size & Quality
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="size">QR Code Size</Label>
                  <Select 
                    value={formData.settings.size.toString()} 
                    onValueChange={(value) => setFormData(prev => ({
                      ...prev,
                      settings: { ...prev.settings, size: parseInt(value) }
                    }))}
                  >
                    <SelectTrigger data-testid="select-size">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="150">Small (150Ã—150)</SelectItem>
                      <SelectItem value="200">Medium (200Ã—200)</SelectItem>
                      <SelectItem value="300">Large (300Ã—300)</SelectItem>
                      <SelectItem value="400">Extra Large (400Ã—400)</SelectItem>
                      <SelectItem value="500">Huge (500Ã—500)</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="error-correction">Error Correction Level</Label>
                  <Select 
                    value={formData.settings.errorCorrectionLevel} 
                    onValueChange={(value: 'L' | 'M' | 'Q' | 'H') => setFormData(prev => ({
                      ...prev,
                      settings: { ...prev.settings, errorCorrectionLevel: value }
                    }))}
                  >
                    <SelectTrigger data-testid="select-error-correction">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="L">L - {getErrorCorrectionDescription('L')}</SelectItem>
                      <SelectItem value="M">M - {getErrorCorrectionDescription('M')}</SelectItem>
                      <SelectItem value="Q">Q - {getErrorCorrectionDescription('Q')}</SelectItem>
                      <SelectItem value="H">H - {getErrorCorrectionDescription('H')}</SelectItem>
                    </SelectContent>
                  </Select>
                  <p className="text-xs text-muted-foreground">
                    Higher error correction allows the QR code to be read even when partially damaged
                  </p>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="margin">Margin</Label>
                  <Select 
                    value={formData.settings.margin.toString()} 
                    onValueChange={(value) => setFormData(prev => ({
                      ...prev,
                      settings: { ...prev.settings, margin: parseInt(value) }
                    }))}
                  >
                    <SelectTrigger data-testid="select-margin">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="0">No margin</SelectItem>
                      <SelectItem value="2">Small margin</SelectItem>
                      <SelectItem value="4">Medium margin</SelectItem>
                      <SelectItem value="6">Large margin</SelectItem>
                      <SelectItem value="10">Extra large margin</SelectItem>
                    </SelectContent>
