                      onChange={(e) => setFormData(prev => ({
                        ...prev,
                        settings: { ...prev.settings, backgroundColor: e.target.value }
                      }))}
                      className="w-16 h-10 p-1 border rounded"
                      data-testid="input-bg-color"
                    />
                    <Input
                      value={formData.settings.backgroundColor}
                      onChange={(e) => setFormData(prev => ({
                        ...prev,
                        settings: { ...prev.settings, backgroundColor: e.target.value }
                      }))}
                      placeholder="#FFFFFF"
                      className="flex-1"
                    />
