                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setFormData(prev => ({
                        ...prev,
                        settings: {
                          ...prev.settings,
                          foregroundColor: '#FFFFFF',
                          backgroundColor: '#000000'
                        }
