import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { BatchProcessor } from "@/components/ui/batch-processor";
import { useClipboard } from "@/hooks/use-clipboard";
import { useKeyboardShortcuts, type KeyboardShortcut } from "@/hooks/use-keyboard-shortcuts";
import { calculateTextStats, type TextStats } from "@/lib/text-utils";
import { 
  Calculator, 
  Type, 
  FileText, 
  List, 
  Hash, 
  Clock, 
  HardDrive,
  Copy,
  Download,
  RotateCcw,
  Trash2,
  Info,
  BookOpen,
  TrendingUp
} from "lucide-react";

const MAX_SIZE_KB = 512;
const MAX_SIZE_BYTES = MAX_SIZE_KB * 1024;

export default function TextCounter() {
  const [text, setText] = useState("");
  const [stats, setStats] = useState<TextStats>(() => calculateTextStats(""));
  const { copyToClipboard } = useClipboard();

  useEffect(() => {
    setStats(calculateTextStats(text));
  }, [text]);

  const handleClear = () => {
    setText("");
  };

  const handleCopyAll = () => {
    const statsText = `Text Statistics:
Characters: ${stats.characters}
Characters (no spaces): ${stats.charactersNoSpaces}
Words: ${stats.words}
Lines: ${stats.lines}
Paragraphs: ${stats.paragraphs}
Sentences: ${stats.sentences}
Reading Time: ${stats.readingTime}
File Size: ${stats.fileSize}`;

    copyToClipboard(statsText, "All statistics copied to clipboard");
  };

  const handleExport = () => {
    const statsData = {
      text: text.substring(0, 100) + (text.length > 100 ? '...' : ''),
      statistics: stats,
      timestamp: new Date().toISOString()
    };

    const dataStr = JSON.stringify(statsData, null, 2);
    const dataBlob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `text-stats-${new Date().toISOString().split('T')[0]}.json`;
    link.click();
    URL.revokeObjectURL(url);
  };

  // Tool-specific keyboard shortcuts
  const shortcuts: KeyboardShortcut[] = [
    {
      key: 'c',
      ctrl: true,
      shift: true,
      action: handleCopyAll,
      description: 'Copy all statistics (Ctrl+Shift+C)'
    },
    {
      key: 'r',
      ctrl: true,
      action: handleClear,
      description: 'Clear text (Ctrl+R)'
    },
    {
      key: 'e',
      ctrl: true,
      shift: true,
      action: handleExport,
      description: 'Export statistics (Ctrl+Shift+E)'
    }
  ];

  useKeyboardShortcuts(shortcuts);

  const currentSizeBytes = new Blob([text]).size;
  const isOverLimit = currentSizeBytes > MAX_SIZE_BYTES;

  const statCards = [
    { icon: Calculator, label: "Characters", value: stats.characters, color: "bg-primary/10 text-primary" },
    { icon: Type, label: "Chars (no spaces)", value: stats.charactersNoSpaces, color: "bg-blue-500/10 text-blue-500" },
    { icon: FileText, label: "Words", value: stats.words, color: "bg-green-500/10 text-green-500" },
    { icon: List, label: "Lines", value: stats.lines, color: "bg-purple-500/10 text-purple-500" },
    { icon: Hash, label: "Paragraphs", value: stats.paragraphs, color: "bg-indigo-500/10 text-indigo-500" },
    { icon: FileText, label: "Sentences", value: stats.sentences, color: "bg-yellow-500/10 text-yellow-500" },
    { icon: Clock, label: "Reading Time", value: stats.readingTime, color: "bg-pink-500/10 text-pink-500" },
    { icon: HardDrive, label: "File Size", value: stats.fileSize, color: "bg-red-500/10 text-red-500" },
  ];

  return (
    <div className="space-y-6">
      {/* Input Section */}
      <Card>
        <CardContent className="p-6">
          <div className="mb-4">
            <label htmlFor="text-input" className="block text-sm font-medium text-foreground mb-2">
              Enter your text to analyze
            </label>
            <div className="relative">
              <Textarea
                id="text-input"
                placeholder="Start typing to see real-time statistics..."
                className="min-h-40 resize-none"
                value={text}
                onChange={(e) => setText(e.target.value)}
                data-testid="textarea-text-input"
              />
              <div className="absolute bottom-3 right-3 text-xs text-muted-foreground">
                <span className={isOverLimit ? "text-destructive font-medium" : ""}>
                  {(currentSizeBytes / 1024).toFixed(1)} KB
                </span> / {MAX_SIZE_KB} KB max
              </div>
            </div>
            <div className="mt-2 flex items-center justify-between text-xs text-muted-foreground">
              <span>Maximum text size: {MAX_SIZE_KB}KB</span>
              <Button 
                variant="ghost" 
                size="sm"
                onClick={handleClear}
                className="text-primary hover:text-primary/80 h-auto p-0"
                data-testid="button-clear"
              >
                <Trash2 className="w-3 h-3 mr-1" />
                Clear
              </Button>
            </div>
            {isOverLimit && (
              <div className="mt-2 text-xs text-destructive">
                Text exceeds maximum size limit of {MAX_SIZE_KB}KB
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Statistics Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {statCards.map((stat, index) => (
          <Card key={stat.label} className="stat-card">
            <CardContent className="p-6">
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center space-x-3">
                  <div className={`w-10 h-10 rounded-lg flex items-center justify-center ${stat.color}`}>
                    <stat.icon className="w-5 h-5" />
                  </div>
                  <div>
                    <p className="text-sm font-medium text-muted-foreground">{stat.label}</p>
                    <p className="text-2xl font-bold text-foreground">{stat.value}</p>
                  </div>
                </div>
                <Button
                  variant="outline"
                  size="sm"
                  className="copy-btn"
                  onClick={() => copyToClipboard(stat.value.toString(), `${stat.label} copied`)}
                  data-testid={`button-copy-${stat.label.toLowerCase().replace(/\s+/g, '-')}`}
                >
                  <Copy className="w-4 h-4" />
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Additional Actions */}
      <Card>
        <CardContent className="p-6">
          <h3 className="text-lg font-semibold text-foreground mb-4">Quick Actions</h3>
          <div className="flex flex-wrap gap-3">
            <BatchProcessor mode="counter" />
            <Button 
              onClick={handleCopyAll}
              data-testid="button-copy-all"
            >
              <Copy className="w-4 h-4 mr-2" />
              Copy All Stats
            </Button>
            <Button 
              variant="secondary"
              onClick={handleExport}
              data-testid="button-export"
            >
              <Download className="w-4 h-4 mr-2" />
              Export Stats
            </Button>
            <Button 
              variant="outline"
              onClick={handleClear}
              data-testid="button-reset"
            >
              <RotateCcw className="w-4 h-4 mr-2" />
              Reset
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Readability Analysis */}
      {stats.readability && (
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center space-x-3 mb-4">
              <BookOpen className="w-5 h-5 text-primary" />
              <h3 className="text-lg font-semibold text-foreground">Readability Analysis</h3>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="text-center p-4 bg-muted rounded-lg">
                <p className="text-sm text-muted-foreground mb-1">Flesch Reading Ease</p>
                <p className="text-2xl font-bold text-foreground">{stats.readability.fleschReadingEase.toFixed(1)}</p>
                <Badge 
                  variant="secondary" 
                  className={`mt-1 ${
                    stats.readability.fleschReadingEase >= 70 
                      ? 'bg-green-100 text-green-700 dark:bg-green-900 dark:text-green-300'
                      : stats.readability.fleschReadingEase >= 50
                      ? 'bg-yellow-100 text-yellow-700 dark:bg-yellow-900 dark:text-yellow-300'
                      : 'bg-red-100 text-red-700 dark:bg-red-900 dark:text-red-300'
                  }`}
                >
                  {stats.readability.readingLevel}
                </Badge>
              </div>
              <div className="text-center p-4 bg-muted rounded-lg">
                <p className="text-sm text-muted-foreground mb-1">Grade Level</p>
                <p className="text-2xl font-bold text-foreground">{stats.readability.fleschKincaidGrade.toFixed(1)}</p>
                <p className="text-xs text-muted-foreground mt-1">Flesch-Kincaid</p>
              </div>
              <div className="flex flex-col justify-center">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => copyToClipboard(
                    `Readability: ${stats.readability?.readingLevel} (${stats.readability?.fleschReadingEase.toFixed(1)} score, Grade ${stats.readability?.fleschKincaidGrade.toFixed(1)})`,
                    "Readability analysis copied"
                  )}
                  data-testid="button-copy-readability"
                >
                  <Copy className="w-4 h-4 mr-2" />
                  Copy Analysis
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Keyword Density */}
      {stats.keywordDensity && stats.keywordDensity.length > 0 && (
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center space-x-3 mb-4">
              <TrendingUp className="w-5 h-5 text-primary" />
              <h3 className="text-lg font-semibold text-foreground">Top Keywords</h3>
              <Badge variant="secondary">{stats.keywordDensity.length} keywords</Badge>
            </div>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-2">
              {stats.keywordDensity.map((item, index) => (
                <div 
                  key={index} 
                  className="flex items-center justify-between p-2 bg-muted rounded-md hover:bg-accent transition-colors cursor-pointer"
                  onClick={() => copyToClipboard(item.keyword, `"${item.keyword}" copied`)}
                  data-testid={`keyword-${index}`}
                >
                  <div className="min-w-0 flex-1">
                    <p className="text-sm font-medium truncate">{item.keyword}</p>
                    <div className="flex items-center space-x-2 text-xs text-muted-foreground">
                      <span>{item.count}x</span>
                      <span>â€¢</span>
                      <span>{item.density.toFixed(1)}%</span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
            <div className="mt-4 pt-3 border-t border-border">
              <Button
                variant="outline"
                size="sm"
                onClick={() => copyToClipboard(
                  stats.keywordDensity?.map(item => `${item.keyword} (${item.count}x, ${item.density.toFixed(1)}%)`).join('\n') || '',
                  "Keyword density analysis copied"
                )}
                data-testid="button-copy-keywords"
              >
                <Copy className="w-4 h-4 mr-2" />
                Copy All Keywords
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Information Panel */}
      <Card className="bg-blue-50 dark:bg-blue-950/20 border-blue-200 dark:border-blue-800">
        <CardContent className="p-4">
          <div className="flex items-start space-x-3">
            <Info className="w-5 h-5 text-blue-500 mt-0.5 flex-shrink-0" />
            <div>
              <h4 className="font-medium text-blue-900 dark:text-blue-100 mb-1">Analysis Information</h4>
              <div className="space-y-1 text-sm text-blue-700 dark:text-blue-200">
                <p>â€¢ Reading time is based on 200 words per minute average</p>
                <p>â€¢ Readability uses Flesch Reading Ease and Flesch-Kincaid Grade Level formulas</p>
                <p>â€¢ Keyword density shows the most frequently used words (3+ characters, appearing 2+ times)</p>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/todos.tsx.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { useToast } from "@/hooks/use-toast";
import { Plus, Search, Trash2, Edit3, Calendar, CheckCircle2 } from "lucide-react";

interface Todo {
  id: string;
  title: string;
  completed: boolean;
  createdAt: Date;
}

export default function Todos() {
  const [todos, setTodos] = useState<Todo[]>([]);
  const [newTodo, setNewTodo] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [editingId, setEditingId] = useState<string | null>(null);
  const [editingText, setEditingText] = useState("");
  const [deleteId, setDeleteId] = useState<string | null>(null);
  const { toast } = useToast();

  // Load todos from localStorage on mount
  useEffect(() => {
    const saved = localStorage.getItem("todos");
    if (saved) {
      try {
        const parsed = JSON.parse(saved);
        setTodos(parsed.map((todo: any) => ({
          ...todo,
          createdAt: new Date(todo.createdAt)
        })));
      } catch (error) {
        console.error("Failed to load todos:", error);
      }
    }
  }, []);

  // Save todos to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  const addTodo = () => {
    if (!newTodo.trim()) {
      toast({
        title: "Error",
        description: "Please enter a todo item",
        variant: "destructive",
      });
      return;
    }

    if (newTodo.trim().length < 3) {
      toast({
        title: "Error",
        description: "Todo must be at least 3 characters long",
        variant: "destructive",
      });
      return;
    }

    const todo: Todo = {
      id: Date.now().toString(),
      title: newTodo.trim(),
      completed: false,
      createdAt: new Date(),
    };

    setTodos(prev => [todo, ...prev]);
    setNewTodo("");

    toast({
      title: "Success",
      description: "Todo added successfully",
    });
  };

  const toggleComplete = (id: string) => {
    setTodos(prev => prev.map(todo => 
      todo.id === id ? { ...todo, completed: !todo.completed } : todo
    ));
  };

  const startEditing = (todo: Todo) => {
    setEditingId(todo.id);
    setEditingText(todo.title);
  };

  const saveEdit = () => {
    if (!editingText.trim()) {
      toast({
        title: "Error",
        description: "Todo cannot be empty",
        variant: "destructive",
      });
      return;
    }

    if (editingText.trim().length < 3) {
      toast({
        title: "Error",
        description: "Todo must be at least 3 characters long",
        variant: "destructive",
      });
      return;
    }

    setTodos(prev => prev.map(todo =>
      todo.id === editingId ? { ...todo, title: editingText.trim() } : todo
    ));

    setEditingId(null);
    setEditingText("");

    toast({
      title: "Success",
      description: "Todo updated successfully",
    });
  };

  const cancelEdit = () => {
    setEditingId(null);
    setEditingText("");
  };

  const deleteTodo = () => {
    if (!deleteId) return;

    setTodos(prev => prev.filter(todo => todo.id !== deleteId));
    setDeleteId(null);

    toast({
      title: "Success",
      description: "Todo deleted successfully",
    });
  };

  const filteredTodos = todos.filter(todo =>
    todo.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const completedCount = todos.filter(todo => todo.completed).length;
  const totalCount = todos.length;

  return (
    <div className="space-y-6">
      {/* Stats */}
      <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
        <Card>
          <CardContent className="p-4 text-center">
            <CheckCircle2 className="w-8 h-8 mx-auto mb-2 text-green-500" />
            <p className="text-2xl font-bold">{completedCount}</p>
            <p className="text-sm text-muted-foreground">Completed</p>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4 text-center">
            <Calendar className="w-8 h-8 mx-auto mb-2 text-blue-500" />
            <p className="text-2xl font-bold">{totalCount - completedCount}</p>
            <p className="text-sm text-muted-foreground">Pending</p>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4 text-center">
            <div className="w-8 h-8 mx-auto mb-2 bg-primary/10 rounded-full flex items-center justify-center">
              <span className="text-primary font-bold">{totalCount}</span>
            </div>
            <p className="text-2xl font-bold">{totalCount}</p>
            <p className="text-sm text-muted-foreground">Total</p>
          </CardContent>
        </Card>
      </div>

      {/* Add New Todo */}
      <Card>
        <CardHeader>
          <CardTitle>Add New Todo</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex gap-2">
            <Input
              value={newTodo}
              onChange={(e) => setNewTodo(e.target.value)}
              placeholder="What needs to be done?"
              onKeyDown={(e) => e.key === 'Enter' && addTodo()}
              data-testid="input-new-todo"
            />
            <Button onClick={addTodo} data-testid="button-add-todo">
              <Plus className="w-4 h-4" />
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Search */}
      <Card>
        <CardContent className="p-4">
          <div className="relative">
            <Search className="absolute left-3 top-3 w-4 h-4 text-muted-foreground" />
            <Input
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Search todos..."
              className="pl-10"
              data-testid="input-search-todos"
            />
          </div>
        </CardContent>
      </Card>

      {/* Todos List */}
      <Card>
        <CardHeader>
          <CardTitle>Todo List</CardTitle>
        </CardHeader>
        <CardContent>
          {filteredTodos.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              {todos.length === 0 ? (
                <p>No todos yet. Add your first todo above!</p>
              ) : (
                <p>No todos match your search.</p>
              )}
            </div>
          ) : (
            <div className="space-y-3">
              {filteredTodos.map((todo) => (
                <div
                  key={todo.id}
                  className={`flex items-center space-x-3 p-3 rounded-lg border transition-colors ${
                    todo.completed 
                      ? 'bg-muted/50 border-muted' 
                      : 'bg-card border-border hover:bg-accent/50'
                  }`}
                >
                  <Checkbox
                    checked={todo.completed}
                    onCheckedChange={() => toggleComplete(todo.id)}
                    data-testid={`checkbox-todo-${todo.id}`}
                  />

                  <div className="flex-1 min-w-0">
                    {editingId === todo.id ? (
                      <div className="flex gap-2">
                        <Input
                          value={editingText}
                          onChange={(e) => setEditingText(e.target.value)}
                          onKeyDown={(e) => {
                            if (e.key === 'Enter') saveEdit();
                            if (e.key === 'Escape') cancelEdit();
                          }}
                          className="flex-1"
                          autoFocus
                          data-testid={`input-edit-todo-${todo.id}`}
                        />
                        <Button size="sm" onClick={saveEdit} data-testid={`button-save-${todo.id}`}>
                          Save
                        </Button>
                        <Button size="sm" variant="outline" onClick={cancelEdit} data-testid={`button-cancel-${todo.id}`}>
                          Cancel
                        </Button>
                      </div>
                    ) : (
                      <div>
                        <p className={`${todo.completed ? 'line-through text-muted-foreground' : ''}`}>
                          {todo.title}
                        </p>
                        <div className="flex items-center gap-2 mt-1">
                          <Badge variant="secondary" className="text-xs">
                            {todo.createdAt.toLocaleDateString()}
                          </Badge>
                          {todo.completed && (
                            <Badge className="text-xs bg-green-500">
                              Completed
                            </Badge>
                          )}
                        </div>
                      </div>
                    )}
                  </div>

                  {editingId !== todo.id && (
                    <div className="flex gap-1">
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => startEditing(todo)}
                        data-testid={`button-edit-${todo.id}`}
                      >
                        <Edit3 className="w-4 h-4" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => setDeleteId(todo.id)}
                        data-testid={`button-delete-${todo.id}`}
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Delete Confirmation Dialog */}
      <AlertDialog open={!!deleteId} onOpenChange={() => setDeleteId(null)}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Delete Todo</AlertDialogTitle>
            <AlertDialogDescription>
              Are you sure you want to delete this todo? This action cannot be undone.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Cancel</AlertDialogCancel>
            <AlertDialogAction onClick={deleteTodo} className="bg-destructive text-destructive-foreground hover:bg-destructive/90">
              Delete
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}

